// components/EmberBackground.js
import React from "react";

function EmberBackground({ children }) {
  return (
    <div className="position-relative min-vh-100 w-100 bg-dark">
      {/* Ember Glow Background */}
      <div
        className="position-absolute top-0 start-0 w-100 h-100 z-0"
        style={{
          backgroundImage: `
            radial-gradient(circle at 50% 100%, rgba(255, 69, 0, 0.6) 0%, transparent 60%),
            radial-gradient(circle at 50% 100%, rgba(255, 140, 0, 0.4) 0%, transparent 70%),
            radial-gradient(circle at 50% 100%, rgba(255, 215, 0, 0.3) 0%, transparent 80%)
          `,
        }}
      />
      {/* Foreground content */}
      <div className="position-relative z-1">{children}</div>
    </div>
  );
}

export default EmberBackground;
