import React, { useEffect, useState } from "react";
import Navbar from "./navbar";
import { useParams } from "react-router-dom";
import axios from "axios";

function Details() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      console.error("No token found in localStorage");
      return;
    }

    // Fetch movie details
    axios
      .get(`http://127.0.0.1:8000/detail/${id}/`, {
        headers: { Authorization: `Token ${token}` },
      })
      .then((response) => setMovie(response.data))
      .catch((error) => console.error("Error fetching movie details:", error));

    // Fetch user's watchlist
    axios
      .get("http://127.0.0.1:8000/watchlist/", {
        headers: { Authorization: `Token ${token}` },
      })
      .then((response) => setWatchlist(response.data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, [id]);

  return (
    <div className="bg-light min-vh-100">
      <Navbar />
      <div className="container py-5">

        {/* Movie Details Section */}
        {movie ? (
          <>
            {/* Video Player */}
            {movie.video ? (
              <div className="mb-4 text-center">
                <video
                  controls
                  className="w-100 rounded shadow"
                  style={{ maxHeight: "500px" }}
                >
                  <source
                    src={`http://127.0.0.1:8000${movie.video}`}
                    type="video/mp4"
                  />
                  Your browser does not support the video tag.
                </video>
              </div>
            ) : (
              <p className="text-center text-muted">No video available</p>
            )}

            {/* Movie Info */}
            <div className="text-center mb-4">
              <h2 className="fw-bold">{movie.title}</h2>
              <p className="text-muted">{movie.desc}</p>
            </div>

            {/* Thumbnail */}
            {movie.thumb && (
              <div className="text-center mb-4">
                <img
                  src={`http://127.0.0.1:8000${movie.thumb}`}
                  alt={movie.title}
                  className="rounded shadow"
                  style={{ maxWidth: "400px" }}
                />
              </div>
            )}
          </>
        ) : (
          <p className="text-center text-muted">Loading movie details...</p>
        )}

        {/* Watchlist Section */}
        <div className="mt-5">
          <h4 className="fw-bold">ðŸŽ¬ Your Watchlist</h4>
          <div className="row mt-3">
            {watchlist.length > 0 ? (
              watchlist.map((item) => (
                <div key={item.id} className="col-md-3 mb-4">
                  <div className="card shadow-sm h-100">
                    {item.thumb && (
                      <img
                        src={`http://127.0.0.1:8000${item.thumb}`}
                        alt={item.title}
                        className="card-img-top"
                        style={{ height: "200px", objectFit: "cover" }}
                      />
                    )}
                    <div className="card-body text-center">
                      <h6 className="fw-bold">{item.title}</h6>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-muted">No movies in your watchlist yet.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Details;
