import React, { useState } from "react";
import Navbar from "./navbar";
import { useParams } from "react-router-dom";

function Details() {

  const {id}=useParams();

  const [movie,setMovie]=useState("title","")

  return (
    <div className="bg-light min-vh-100">
      <Navbar />
      <div className="container py-5">
        {/* Video Player */}
        {videoUrl && (
          <div className="mb-4 text-center">
            <video controls className="w-100 rounded shadow" style={{ maxHeight: '500px' }}>
              <source src={videoUrl} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        )}

        {/* Movie Name & Description */}
        <div className="text-center mb-4">
          <h2 className="fw-bold">{name || "No Name"}</h2>
          <p className="text-muted">{description || "No Description"}</p>
        </div>

        {/* Watchlist Button */}
        <div className="text-center">
          <button className="btn btn-primary btn-lg">Add to Watchlist</button>
        </div>
      </div>
    </div>
  );
}

export default Details;
