import React, { useEffect, useState } from "react";
import Navbar from "./componentnavbar";

function Watchlist() {
  const [watchlist, setWatchlist] = useState([]);

  // Simulate fetching data in future (API call)
  useEffect(() => {
    // Example placeholder for future API call
    // fetch('/api/watchlist').then(res => res.json()).then(data => setWatchlist(data));

    // For now, just set empty details
    setWatchlist([
      { videoUrl: null, name: null, description: null },
      { videoUrl: null, name: null, description: null },
      { videoUrl: null, name: null, description: null },
    ]);
  }, []);

  return (
    <div className="bg-light min-vh-100">
      <Navbar />
      <div className="container py-5">
        <h2 className="fw-bold text-center mb-5">My Watchlist</h2>
        <div className="row">
          {watchlist.map((item, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card shadow h-100">
                {item.videoUrl ? (
                  <video controls className="card-img-top" style={{ maxHeight: '200px' }}>
                    <source src={item.videoUrl} type="video/mp4" />
                  </video>
                ) : (
                  <div className="card-img-top bg-secondary d-flex align-items-center justify-content-center" style={{ height: '200px', color: 'white' }}>
                    No Video
                  </div>
                )}

                <div className="card-body text-center">
                  <h5 className="card-title">{item.name || "No Name"}</h5>
                  <p className="card-text text-muted">{item.description || "No Description"}</p>
                  <button className="btn btn-primary">Add to Watchlist</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Watchlist;
