  const [movie,setMovie]=useState({title:"",desc:"",thumb:"",video:""})
  
  useEffect(()=>{
    const token=localStorage.getItem("token")
    axios.get(`http://127.0.0.1:8000/detail/${id}/`,{headers:{Authorization:"Token"+token},})
  
    .then((response)=>setMovie(response.data))
    .cache((error)=>console.error("error ayi myr",error))},[id]);


