          <p className="text-muted">{description || "No Description"}</p>
        </div>

        {/* Watchlist Button */}
        <div className="text-center">
          <button className="btn btn-primary btn-lg">Add to Watchlist</button>
        </div>
      </div>
    </div>
  );
}

export default Details;
