import React, { useEffect, useState } from "react";
import Navbar from "./navbar";
import { useParams } from "react-router-dom";
import axios from "axios";

function Details() {
  const { id } = useParams();

  const [movie, setMovie] = useState({
    title: "",
    desc: "",
    thumb: "",
    video: "",
  });
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch movie details + watchlist
  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("No token found in localStorage");
        return;
      }

      try {
        // Fetch movie details
        const movieRes = await axios.get(`http://127.0.0.1:8000/detail/${id}/`, {
          headers: { Authorization: `Token ${token}` },
        });
        setMovie(movieRes.data);

        // Fetch user watchlist
        const watchRes = await axios.get("http://127.0.0.1:8000/watchlist/", {
          headers: { Authorization: `Token ${token}` },
        });
        setWatchlist(watchRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  // Add to watchlist
  const handleAddToWatchlist = async () => {
    const token = localStorage.getItem("token");

    if (!token) {
      alert("Please login first!");
      return;
    }

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/watchlist/add/",
        { movie_id: id },
        {
          headers: { Authorization: `Token ${token}` },
        }
      );
      alert("‚úÖ Movie added to watchlist!");
      setWatchlist((prev) => [...prev, movie]);
    } catch (err) {
      console.error("Error adding to watchlist:", err);
      alert("‚ùå Failed to add to watchlist");
    }
  };

  if (loading) {
    return (
      <div className="text-center mt-5">
        <h4>Loading...</h4>
      </div>
    );
  }

  return (
    <div className="bg-light min-vh-100">
      <Navbar />
      <div className="container py-5">
        {/* Video Player */}
        {movie.video ? (
          <div className="mb-4 text-center">
            <video
              controls
              className="w-100 rounded shadow"
              style={{ maxHeight: "500px" }}
            >
              <source
                src={`http://127.0.0.1:8000${movie.video}`}
                type="video/mp4"
              />
              Your browser does not support the video tag.
            </video>
          </div>
        ) : (
          <p className="text-center text-muted">No video available</p>
        )}

        {/* Movie Info */}
        <div className="text-center mb-4">
          <h2 className="fw-bold">{movie.title || "No Title"}</h2>
          <p className="text-muted">{movie.desc || "No Description"}</p>
        </div>

        {/* Thumbnail */}
        {movie.thumb && (
          <div className="text-center mb-4">
            <img
              src={`http://127.0.0.1:8000${movie.thumb}`}
              alt={movie.title}
              className="rounded shadow"
              style={{ maxWidth: "400px" }}
            />
          </div>
        )}

        {/* Add to Watchlist */}
        <div className="text-center mb-5">
          <button onClick={handleAddToWatchlist} className="btn btn-primary btn-lg">
            ‚ûï Add to Watchlist
          </button>
        </div>

        {/* Watchlist Section */}
        <div className="mt-5">
          <h4 className="fw-bold text-center">üé¨ Your Watchlist</h4>
          <div className="row mt-3">
            {watchlist.length > 0 ? (
              watchlist.map((item) => (
                <div key={item.id} className="col-md-3 mb-4">
                  <div className="card shadow-sm h-100">
                    {item.thumb && (
                      <img
                        src={`http://127.0.0.1:8000${item.thumb}`}
                        alt={item.title}
                        className="card-img-top"
                        style={{ height: "200px", objectFit: "cover" }}
                      />
                    )}
                    <div className="card-body text-center">
                      <h6 className="fw-bold">{item.title}</h6>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-muted text-center">
                No movies in your watchlist yet.
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Details;
